// Utilidad para timestamp
function obtenerTimestamp(fechaStr) {
    const [fecha, hora] = fechaStr.split("_");
    const utcDate = new Date(`${fecha}T${hora}Z`); // Interpreta como UTC
    const offset = new Date().toLocaleString("en-US", { timeZone: "America/Bogota" });
    const localDate = new Date(offset);
    const delta = localDate.getTime() - new Date().getTime();
    return utcDate.getTime() + delta;
}

