if (!anterior) return [null, null, null, null, null, null, null, null];

let deltaMed = medicion - anterior.medicion;
let deltaSeg = (ts - anterior.ts) / 1000;
if (deltaSeg <= 0) return [null, null, null, null, null, null, null, null];

let caudal = deltaMed / deltaSeg;
let punto = { x: ts, y: caudal };

buffer[loc].push(punto);
if (buffer[loc].length > 500) buffer[loc].shift();
