function ajustarZonaHoraria(utcString) {
    let fechaUTC = new Date(utcString);
    return fechaUTC.toLocaleString("es-CO", {
        timeZone: "America/Bogota",
        hour12: false,
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
