let sql = "INSERT INTO DB_MacProp (`FECHA_HORA`, `MEDICION`, `UNIDAD`, `LOCALIZACION`) VALUES ";
let filas = buffer.map(d => `('${d.FechaHora}', ${d.Medicion}, ${d.UndMedida}, ${d.Localizacion})`);
sql += filas.join(", ") + ";";

flow.set("buffer", []);  // limpiar
return { topic: sql };
